import React from "react";
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";
const responsive = {
  desktop: {
    breakpoint: { max: 3000, min: 1024 },
    items: 3,
    slidesToSlide: 3, // optional, default to 1.
  },
  tablet: {
    breakpoint: { max: 1024, min: 464 },
    items: 2,
    slidesToSlide: 2, // optional, default to 1.
  },
  mobile: {
    breakpoint: { max: 464, min: 0 },
    items: 1,
    slidesToSlide: 1, // optional, default to 1.
  },
};
const Card = ({ book }) => {
  console.log(book);

  return (
    <>
      {book.map((item) => {
        let thumbnail =
          item.volumeInfo.imageLinks &&
          item.volumeInfo.imageLinks.smallThumbnail;

        let amount = item.saleInfo.listPrice && item.saleInfo.listPrice.amount;

        if (thumbnail != undefined && amount != undefined) {
          return (
            <>
              <Carousel
                swipeable={false}
                draggable={false}
                showDots={true}
                responsive={responsive}
                ssr={true} // means to render carousel on server-side.
                infinite={true}
                autoPlay={this.props.deviceType !== "mobile" ? true : false}
                autoPlaySpeed={1000}
                keyBoardControl={true}
                customTransition="all .5"
                transitionDuration={500}
                containerClass="carousel-container"
                removeArrowOnDeviceType={["tablet", "mobile"]}
                deviceType={this.props.deviceType}
                dotListClass="custom-dot-list-style"
                itemClass="carousel-item-padding-40-px"
              >
                <div className="bg-slate-200 px-8 py-4 rounded-xl overflow-hidden max-w-sm mt-10 transition ease-in-out delay-150 cursor-pointer hover:-translate-y-1 hover:scale-110 hover:bg-slate-300 duration-300">
                  <img className=" h-40 rounded-xl " src={thumbnail} />
                  <div>
                    <h1 className="text-gray-700 font-bold text-2xl mt-5">
                      {item.volumeInfo.title}
                    </h1>
                    <p>{item.volumeInfo.authors}</p>
                    <p>{item.volumeInfo.publisher}</p>
                    <p>{item.volumeInfo.publishedDate}</p>
                    <a href={item.volumeInfo.previewLink}>
                      <button>More</button>
                    </a>
                  </div>
                  <p>{amount}</p>
                </div>
              </Carousel>
            </>
          );
        }
      })}
    </>
  );
};

export default Card;
