import React, { useState, useEffect } from "react";
import Axios from "axios";

function Book() {
  const [listOfBook, setListOfBook] = useState([]);
  const [search, setSearch] = useState("");

  const searchBook = (evt) => {
    if (evt.key === "Enter") {
      Axios.get(
        "https://www.googleapis.com/books/v1/volumes?q='+search+'%7Bkeyword"
      )
        .then((response) => setListOfBook(response.data.items))
        .catch((error) => console.log(error));
    }
  };

  return (
    <div className=" mt-20 px-8 py-4">
      <div className="grid grid-cols-12 ml-20">
        <div className="col-span-3">
          <span className="text-purple-800 font-semibold text-3xl">
            Popular Now
          </span>
        </div>
        <div className="col-span-6">
          <input
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            onKeyPress={searchBook}
            type="text"
            className="w-11/12 px-8 py-2 outline-none bg-transparent bg-slate-200 rounded-lg"
            placeholder="Find the book you are looking for"
          />
        </div>
        <div className="col-span-3 ml-20">
          <span className="text-gray-400 font-semibold text-lg">View More</span>
        </div>
      </div>
      <div className="px-8 py-4 flex gap-4">
        {listOfBook.map((listOfBook, index) => {
          return (
            <div>
              <p>{listOfBook.title}</p>
            </div>
          );
        })}

        {/* <div className="bg-slate-200 px-8 py-4 rounded-xl overflow-hidden max-w-sm mt-10">
          <img className="w-full rounded-xl" src="images/cover.jpg" />
          <div>
            <h1 className="text-gray-700 font-bold text-2xl mt-5">Selena</h1>
            <h2>4.1</h2>
            <h1 className="text-gray-400 text-lg">Tere Liye</h1>
          </div>
        </div> */}
      </div>
    </div>
  );
}

export default Book;
