import React from "react";
import Carousel from "react-elastic-carousel";

const Card = ({ book }) => {
  const breakPoints = [
    { width: 1, itemsToShow: 1 },
    { width: 550, itemsToShow: 2 },
    { width: 768, itemsToShow: 3 },
    { width: 1200, itemsToShow: 4 },
  ];
  console.log(book);

  return (
    <>
      <Carousel breakPoints={breakPoints}>
        {book.map((item) => {
          let thumbnail =
            item.volumeInfo.imageLinks &&
            item.volumeInfo.imageLinks.smallThumbnail;

          let amount =
            item.saleInfo.listPrice && item.saleInfo.listPrice.amount;

          if (thumbnail != undefined && amount != undefined) {
            return (
              <>
                <div className="bg-slate-200 px-8 py-4 rounded-xl overflow-hidden max-w-sm mt-10 transition ease-in-out delay-150 cursor-pointer hover:-translate-y-1 hover:scale-90 hover:bg-slate-300 duration-300">
                  <img className=" h-40 rounded-xl " src={thumbnail} />
                  <div>
                    <h1 className="text-gray-700 font-bold text-2xl mt-5">
                      {item.volumeInfo.title}
                    </h1>
                    <p>{item.volumeInfo.authors}</p>
                    <p>{item.volumeInfo.publisher}</p>
                    <p>{item.volumeInfo.publishedDate}</p>
                    <a href={item.volumeInfo.previewLink}>
                      <button>More</button>
                    </a>
                  </div>
                  <p>{amount}</p>
                </div>
              </>
            );
          }
        })}
      </Carousel>
    </>
  );
};

export default Card;
