import React, { useState, useEffect } from "react";
import axios from "axios";
import Card from "./Card";
import Carousel from "react-multi-carousel";
import "react-multi-carousel/lib/styles.css";
function ListOfBooks() {
  const [bookData, setData] = useState([]);
  const [search, setSearch] = useState("");

  const responsive = {
    desktop: {
      breakpoint: { max: 3000, min: 1024 },
      items: 3,
      slidesToSlide: 3, // optional, default to 1.
    },
    tablet: {
      breakpoint: { max: 1024, min: 464 },
      items: 2,
      slidesToSlide: 2, // optional, default to 1.
    },
    mobile: {
      breakpoint: { max: 464, min: 0 },
      items: 1,
      slidesToSlide: 1, // optional, default to 1.
    },
  };

  const searchBook = (evt) => {
    if (evt.key === "Enter") {
      axios
        .get(
          "https://www.googleapis.com/books/v1/volumes?q='+search+'%7Bkeyword" +
            "&maxResults=40"
          // "https://www.googleapis.com/books/v1/volumes?q='+search+'%7Bkeyword"
        )
        .then((res) => setData(res.data.items))
        .catch((err) => console.log(err));
    }
  };

  return (
    <div className=" mt-20 px-8 py-4">
      <div className="grid grid-cols-12 ml-20">
        <div className="col-span-3">
          <span className="text-purple-800 font-semibold text-3xl">
            Popular Now
          </span>
        </div>
        <div className="col-span-6">
          <input
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            onKeyPress={searchBook}
            type="text"
            className="w-11/12 px-8 py-2 outline-none bg-transparent bg-slate-200 rounded-lg"
            placeholder="Find the book you are looking for"
          />
        </div>
        <div className="col-span-3 ml-20">
          <span className="text-gray-400 font-semibold text-lg">View More</span>
        </div>
      </div>
      <div className="px-8 py-4 flex gap-8 bg-blue-200 w-11/12">
        <Carousel
          swipeable={false}
          draggable={false}
          showDots={true}
          responsive={responsive}
          ssr={true} // means to render carousel on server-side.
          infinite={true}
          autoPlay={this.props.deviceType !== "mobile" ? true : false}
          autoPlaySpeed={1000}
          keyBoardControl={true}
          customTransition="all .5"
          transitionDuration={500}
          containerClass="carousel-container"
          removeArrowOnDeviceType={["tablet", "mobile"]}
          deviceType={this.props.deviceType}
          dotListClass="custom-dot-list-style"
          itemClass="carousel-item-padding-40-px"
        >
          <Card book={bookData} />
        </Carousel>
        ;
        {/* {bookData.map((item) => {
          let thumbnail =
            item.volumeInfo.imageLinks &&
            item.volumeInfo.imageLinks.smallThumbnail;

          return (
            <div>
              <img src={thumbnail} />
              <p>{bookData.title}</p>
            </div>
          );
        })} */}
        {/* <div className="bg-slate-200 px-8 py-4 rounded-xl overflow-hidden max-w-sm mt-10">
          <img className="w-full rounded-xl" src="images/cover.jpg" />
          <div>
            <h1 className="text-gray-700 font-bold text-2xl mt-5">Selena</h1>
            <h2>4.1</h2>
            <h1 className="text-gray-400 text-lg">Tere Liye</h1>
          </div>
        </div> */}
      </div>
    </div>
  );
}

export default ListOfBooks;
